# Import the pymysql libraryimport pymysql# Define the connection stringconnection_string = {    "host": "localhost",    "user": "root",    "password": "root",    "database": "my_database",}# Define the register functiondef register():    name = input("نام خود را وارد کنید: ")    username = input("نام کاربری خود را وارد کنید: ")    password = input("رمز عبور خود را وارد کنید: ")    with pymysql.connect(**connection_string) as connection:        cursor = connection.cursor()        cursor.execute("""            INSERT INTO users (name, username, password)            VALUES (%s, %s, %s)        """, (name, username, password))        connection.commit()    print("ثبت نام با موفقیت انجام شد!")# Call the register functionregister()